function MeasurementLength(a){a=a||{},this.inches=a.inches||0,this.feet=a.feet||0,this.yards=a.yards||0,this.centimeters=a.centimeters||0,this.meters=a.meters||0}function getFormValues(){var a=$(".inches-input").val(),b=$(".feet-input").val(),c=$(".yards-input").val(),d=$(".centimeters-input").val(),e=$(".meters-input").val();return input=new MeasurementLength,input.inches=a,input.feet=b,input.yards=c,input.centimeters=d,input.meters=e,input}function fillObject(a){return a.inches>0?(a.feet=a.inches/12,a.yards=a.inches/36,a.centimeters=2.54*a.inches,a.meters=a.centimeters/100):a.feet>0?(a.inches=12*a.feet,a.yards=a.feet/3,a.centimeters=2.54*a.inches,a.meters=a.centimeters/100):a.yards>0?(a.inches=36*a.yards,a.feet=3*a.yards,a.centimeters=2.54*a.inches,a.meters=a.centimeters/100):a.centimeters>0?(a.inches=a.centimeters/2.54,a.feet=a.inches/12,a.yards=a.inches/36,a.meters=a.centimeters/100):a.meters>0&&(a.centimeters=100*a.meters,a.inches=a.centimeters/2.54,a.feet=a.inches/12,a.yards=a.inches/36),a}function conversionListUpdate(a){var b=$(".conversion-list ul");b.html("");var c="<li>"+a.inches+" inches </li> <li>"+a.feet+" feet </li> <li> "+a.yards+" yards </li> <li> "+a.centimeters+" centimeters </li> <li>"+a.meters+" meters </li>";b.append(c)}$(".btn-success").click(function(){getFormValues(),fillObject(input),conversionListUpdate(input),$("input-inches").val(input.inches),$("input-feet").val(input.feet),$("input-yards").val(input.yards)});